--CREAMOS LAS TABLAS.
CREATE TABLE "cancha" ("id_cancha" INT, "nombre" VARCHAR(50));
CREATE TABLE "categoria" ("id_categoria" INT, "nombre" VARCHAR(50), "edad_minima" INT, "edad_maxima" INT);
CREATE TABLE "comuna" ("nombre" VARCHAR(100), "id" INT);
CREATE TABLE "equipo" ("id_equipo" INT, "nombre" VARCHAR(50), "cantidad_jugador" INT, "fecha_creacion" DATE, "id_categoria" INT);
CREATE TABLE "evento" ("id_evento" INT, "id_partido" INT, "rut_jugador" INT, "minuto" INT, "equipo_local" VARCHAR(50), "equipo_visita" VARCHAR(50), "equipo_jugador" VARCHAR(50), "id_tipo_evento" VARCHAR(2), "fecha" DATE, "nombre_jugador" VARCHAR(50), "apellido_jugador" VARCHAR(50), "camiseta" INT);
CREATE TABLE "jugador" ("rut" INT, "dv" CHAR(1), "nombre" VARCHAR(50), "apellido" VARCHAR(50));
CREATE TABLE "jugador_equipo" ("id_jugador_equipo" INT, "fecha_incorporacion" DATE, "camiseta" VARCHAR(3), "id_equipo" INT, "rut_jugador" INT);
CREATE TABLE "partido" ("id_partido" INT, "id_programacion" INT, "inicio" TIMESTAMP (6), "fin" TIMESTAMP (6), "goles_local" INT, "goles_visita" INT, "id_equipo_local" INT, "id_equipo_visita" INT, "id_user" INT, "id_cancha" INT, "fecha" DATE, "equipo_local" VARCHAR(50), "equipo_visita" VARCHAR(50), "cancha" VARCHAR(50), "id_tarifa" INT DEFAULT 1);
CREATE TABLE "programacion" ("id_programacion" INT, "id_equipo_1" INT, "id_equipo_2" INT, "fecha" DATE, "hora" TIMESTAMP (6), "id_tarifa" INT, "valor_pagado" INT DEFAULT 0, "estado" CHAR(2) DEFAULT 'NJ', "equipo_1" VARCHAR(50), "equipo_2" VARCHAR(50), "minutos_jugados" INT);
CREATE TABLE "tarifa" ("id_tarifa" INT, "descripcion" VARCHAR(50), "valor" INT, "fecha_creacion" DATE, "fecha_actualizacion" DATE, "fecha_eliminacion" DATE, "id_user_creacion" INT, "id_user_actualizacion" INT, "id_user_eliminacion" INT);
CREATE TABLE "tipo_evento" ("descripcion" VARCHAR(30), "relevante" CHAR(1) DEFAULT 'N', "id_tipo_evento" VARCHAR(2));
CREATE TABLE "usuario" ("id_user" INT, "username" VARCHAR(50), "password" VARCHAR(50), "fecha_creacion" DATE, "fecha_bloqueo" DATE, "nombre" VARCHAR(50), "apellido" VARCHAR(50));

